```
#include <iostream>
#include <cstdlib>
#include <ctime>
#include <vector>

bool is_sorted(const std::vector<int>& nums) {
    for (int i = 0; i < nums.size() - 1; i++) {
        if (nums[i] > nums[i + 1]) {
            return false;
        }
    }
    return true;
}

void bogosort(std::vector<int>& nums) {
    srand(time(NULL)); // seed the random number generator
    while (!is_sorted(nums)) {
        for (int i = 0; i < nums.size(); i++) {
            int random_index = rand() % nums.size();
            std::swap(nums[i], nums[random_index]);
        }
    }
}

int main() {
    std::vector<int> nums = {3, 1, 4, 1, 5, 9, 2, 6};
    bogosort(nums);
    for (int num : nums) {
        std::cout << num << " ";
    }
    std::cout << std::endl;
    return 0;
}
```

