```cpp
#include <iostream>
#include <vector>

std::vector<int> findLongestIncreasingSequence(const std::vector<int>& vec) {
    int n = vec.size();
    std::vector<int> dp(n, 1), prev(n, -1);
    int bestIndex = 0, bestLength = 1;

    for (int i = 1; i < n; i++) {
        for (int j = 0; j < i; j++) {
            if (vec[j] < vec[i] && dp[j] + 1 > dp[i]) {
                dp[i] = dp[j] + 1;
                prev[i] = j;
            }
        }
        if (dp[i] > bestLength) {
            bestIndex = i;
            bestLength = dp[i];
        }
    }

    std::vector<int> ret;
    for (int i = 0; i < bestLength; i++) {
        ret.push_back(vec[bestIndex]);
        bestIndex = prev[bestIndex];
    }
    std::reverse(ret.begin(), ret.end());
    return ret;
}
```

